mapper.select:
select * from (select * from townBuyBoard where premium = 1 and process = 1 
and category = #{param1} and address like '${param3}%' order by dbms_random.value) 
where rownum  <=  ${param2}

mapper.update:
UPDATE townBuyBoard
SET personnelNow = personnelNow + 1
WHERE num = #{param1}

UPDATE townBuyBoard
SET process =  0
WHERE num = #{param1}
